import React, { useState } from "react";

export default function TACGenerator() {
  const [code, setCode] = useState("");
  const [tac, setTac] = useState("");

  const generateTAC = async () => {
    const response = await fetch("http://localhost:5000/generate-tac", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ code }),
    });
    const data = await response.json();
    setTac(data.tac);
  };

  return (
    <div
      style={{
        display: "flex",
        height: "100vh",
        background: "linear-gradient(45deg, #0f0f0f, #1a1a1a)",
        color: "white",
        fontFamily: "monospace",
      }}
    >
      {/* Left Side - Input */}
      <div
        style={{
          flex: 1,
          padding: "20px",
          borderRight: "2px solid #ff00ff",
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
        }}
      >
        <h2 style={{ textShadow: "0 0 5px #ff00ff" }}>ðŸ’» Enter Code</h2>
        <textarea
          rows="15"
          cols="50"
          placeholder="Enter C-like code..."
          value={code}
          onChange={(e) => setCode(e.target.value)}
          style={{
            width: "90%",
            padding: "10px",
            borderRadius: "8px",
            background: "#222",
            color: "#0f0",
            fontSize: "1rem",
            border: "2px solid #ff00ff",
          }}
        />
        <button
          onClick={generateTAC}
          style={{
            marginTop: "15px",
            padding: "10px 20px",
            fontSize: "1rem",
            fontWeight: "bold",
            borderRadius: "8px",
            border: "none",
            background: "linear-gradient(90deg, #ff0000, #ffcc00)",
            color: "white",
            cursor: "pointer",
            transition: "0.3s",
          }}
          onMouseOver={(e) => (e.target.style.transform = "scale(1.1)")}
          onMouseOut={(e) => (e.target.style.transform = "scale(1)")}
        >
          ðŸš€ Generate TAC ðŸš€
        </button>
      </div>

      {/* Right Side - Output */}
      <div
        style={{
          flex: 1,
          padding: "20px",
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
        }}
      >
        <h2 style={{ textShadow: "0 0 5px #00ffff" }}>âš¡ Generated TAC âš¡</h2>
        <div
          style={{
            width: "90%",
            minHeight: "60vh",
            padding: "10px",
            borderRadius: "8px",
            background: "#222",
            color: "#ffcc00",
            fontSize: "1rem",
            border: "2px solid #00ffff",
            overflow: "auto",
            whiteSpace: "pre-wrap",
          }}
        >
          {tac ? <pre>{tac}</pre> : <p style={{ opacity: 0.5 }}>TAC will be displayed here...</p>}
        </div>
      </div>
    </div>
  );
}
