import { Button } from "@/components/ui/button";
import { Card } from "@/components/ui/card";
import { Textarea } from "@/components/ui/textarea";
import { useState } from "react";

export default function TACGenerator() {
  const [code, setCode] = useState("");
  const [tac, setTac] = useState("");

  const generateTAC = async () => {
    const response = await fetch("http://localhost:5000/generate-tac", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ code }),
    });
    const data = await response.json();
    setTac(data.tac);
  };

  return (
    <div className="p-4 flex flex-col items-center">
      <h1 className="text-2xl font-bold mb-4">Three-Address Code Generator</h1>
      <Card className="w-1/2 p-4">
        <Textarea
          rows="6"
          placeholder="Enter C-like code (for, while, if-else)..."
          value={code}
          onChange={(e) => setCode(e.target.value)}
        />
        <Button onClick={generateTAC} className="mt-2 w-full">
          Generate TAC
        </Button>
      </Card>

      {tac && (
        <Card className="w-1/2 mt-4 p-4">
          <h2 className="text-xl font-semibold">Generated Three-Address Code</h2>
          <pre className="bg-gray-200 p-2 rounded mt-2">{tac}</pre>
        </Card>
      )}
    </div>
  );
}
