import React, { useState } from "react";

export default function TACGenerator() {
  const [code, setCode] = useState("");
  const [tac, setTac] = useState("");

  const generateTAC = async () => {
    const response = await fetch("http://localhost:5000/generate-tac", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ code }),
    });
    const data = await response.json();
    setTac(data.tac);
  };

  return (
    <div
      style={{
        textAlign: "center",
        marginTop: "20px",
        background: "linear-gradient(45deg, #ff00ff, #00ffff, #ffcc00)",
        padding: "20px",
        minHeight: "100vh",
        color: "white",
        fontFamily: "Comic Sans MS, cursive",
      }}
    >
      <h1
        style={{
          fontSize: "3rem",
          textShadow: "4px 4px 10px black",
          animation: "glow 1s infinite alternate",
        }}
      >
        ðŸŽ¨ Crazy TAC Generator ðŸŽ¨
      </h1>

      <textarea
        rows="6"
        cols="50"
        placeholder="Enter C-like code..."
        value={code}
        onChange={(e) => setCode(e.target.value)}
        style={{
          width: "80%",
          padding: "10px",
          marginTop: "10px",
          borderRadius: "10px",
          border: "2px solid #ff00ff",
          backgroundColor: "rgba(0, 0, 0, 0.7)",
          color: "#00ffff",
          fontSize: "1.2rem",
          textAlign: "center",
        }}
      />
      <br />

      <button
        onClick={generateTAC}
        style={{
          marginTop: "20px",
          padding: "15px 30px",
          fontSize: "1.5rem",
          fontWeight: "bold",
          borderRadius: "50px",
          border: "none",
          background: "linear-gradient(90deg, #ff0000, #ffcc00)",
          color: "white",
          cursor: "pointer",
          transition: "transform 0.2s, box-shadow 0.2s",
          boxShadow: "0 5px 15px rgba(0, 0, 0, 0.5)",
        }}
        onMouseOver={(e) => (e.target.style.transform = "scale(1.1)")}
        onMouseOut={(e) => (e.target.style.transform = "scale(1)")}
      >
        ðŸš€ Generate TAC ðŸš€
      </button>

      {tac && (
        <div
          style={{
            marginTop: "20px",
            padding: "15px",
            border: "3px dashed #00ff00",
            borderRadius: "15px",
            backgroundColor: "rgba(0, 0, 0, 0.8)",
            color: "#ffcc00",
            fontSize: "1.3rem",
            textAlign: "left",
            width: "80%",
            margin: "20px auto",
            boxShadow: "0 0 15px #ff00ff",
          }}
        >
          <h2
            style={{
              textAlign: "center",
              textShadow: "2px 2px 8px black",
              color: "#ff00ff",
            }}
          >
            âš¡ Generated TAC âš¡
          </h2>
          <pre style={{ whiteSpace: "pre-wrap" }}>{tac}</pre>
        </div>
      )}

      {/* Adding CSS Animation for the Glow Effect */}
      <style>
        {`
          @keyframes glow {
            from {
              text-shadow: 0 0 10px #ff00ff, 0 0 20px #ff00ff;
            }
            to {
              text-shadow: 0 0 20px #ff00ff, 0 0 30px #ff00ff;
            }
          }
        `}
      </style>
    </div>
  );
}
