import React, { useState } from "react";

export default function TACGenerator() {
  const [code, setCode] = useState("");
  const [tac, setTac] = useState("");

  const generateTAC = async () => {
    const response = await fetch("http://localhost:5000/generate-tac", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ code }),
    });
    const data = await response.json();
    setTac(data.tac);
  };

  return (
    <div style={{ textAlign: "center", marginTop: "20px" }}>
      <h1>Three-Address Code Generator</h1>
      <textarea
        rows="6"
        cols="50"
        placeholder="Enter C-like code..."
        value={code}
        onChange={(e) => setCode(e.target.value)}
        style={{ width: "80%", padding: "10px", marginTop: "10px" }}
      />
      <br />
      <button onClick={generateTAC} style={{ marginTop: "10px", padding: "10px" }}>
        Generate TAC
      </button>

      {tac && (
        <div style={{ marginTop: "20px", padding: "10px", border: "1px solid black" }}>
          <h2>Generated TAC</h2>
          <pre>{tac}</pre>
        </div>
      )}
    </div>
  );
}
