import React, { useEffect, useState } from "react";

export default function TACGenerator() {
  const [code, setCode] = useState("");
  const [tac, setTac] = useState("");
  const [animate, setAnimate] = useState(false);

  useEffect(() => {
    setTimeout(() => setAnimate(true), 500); // Trigger animation on load
  }, []);

  const generateTAC = async () => {
    const response = await fetch("http://localhost:5000/generate-tac", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ code }),
    });
    const data = await response.json();
    setTac(data.tac);
  };

  return (
    <div
      style={{
        display: "flex",
        height: "100vh",
        background: "black",
        color: "white",
        fontFamily: "monospace",
        overflow: "hidden",
      }}
    >
      {/* Left Side - Input */}
      <div
        style={{
          flex: 1,
          padding: "20px",
          borderRight: "3px solid #ff00ff",
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
          transform: animate ? "translateX(0)" : "translateX(-100%)",
          transition: "transform 0.8s ease-in-out",
        }}
      >
        <h2 style={{ textShadow: "0 0 8px #ff00ff", animation: "glow 1.5s infinite alternate" }}>
          ðŸ’» Enter Code
        </h2>
        <textarea
          rows="15"
          cols="50"
          placeholder="Enter C-like code..."
          value={code}
          onChange={(e) => setCode(e.target.value)}
          style={{
            width: "90%",
            padding: "12px",
            borderRadius: "8px",
            background: "#222",
            color: "#0f0",
            fontSize: "1.1rem",
            border: "2px solid #ff00ff",
            boxShadow: "0 0 10px #ff00ff",
          }}
        />
        <button
          onClick={generateTAC}
          style={{
            marginTop: "20px",
            padding: "12px 25px",
            fontSize: "1.2rem",
            fontWeight: "bold",
            borderRadius: "8px",
            border: "none",
            background: "linear-gradient(90deg, #ff0000, #ffcc00)",
            color: "white",
            cursor: "pointer",
            transition: "0.3s",
            boxShadow: "0 0 15px #ffcc00",
          }}
          onMouseOver={(e) => {
            e.target.style.transform = "scale(1.1)";
            e.target.style.boxShadow = "0 0 20px #ffcc00";
          }}
          onMouseOut={(e) => {
            e.target.style.transform = "scale(1)";
            e.target.style.boxShadow = "0 0 15px #ffcc00";
          }}
        >
          ðŸš€ Generate TAC ðŸš€
        </button>
      </div>

      {/* Right Side - Output */}
      <div
        style={{
          flex: 1,
          padding: "20px",
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
          transform: animate ? "translateX(0)" : "translateX(100%)",
          transition: "transform 0.8s ease-in-out",
        }}
      >
        <h2 style={{ textShadow: "0 0 8px #00ffff", animation: "glow 1.5s infinite alternate" }}>
          âš¡ Generated TAC âš¡
        </h2>
        <div
          style={{
            width: "90%",
            minHeight: "60vh",
            padding: "12px",
            borderRadius: "10px",
            background: "#222",
            color: "#ffcc00",
            fontSize: "1.1rem",
            border: "2px solid #00ffff",
            boxShadow: "0 0 10px #00ffff",
            overflow: "auto",
            whiteSpace: "pre-wrap",
            transition: "0.3s",
          }}
        >
          {tac ? <pre>{tac}</pre> : <p style={{ opacity: 0.5 }}>TAC will be displayed here...</p>}
        </div>
      </div>

      {/* CSS Animations */}
      <style>
        {`
          @keyframes glow {
            from {
              text-shadow: 0 0 10px #ff00ff, 0 0 20px #ff00ff;
            }
            to {
              text-shadow: 0 0 20px #ff00ff, 0 0 30px #ff00ff;
            }
          }
        `}
      </style>
    </div>
  );
}
